						$msg = '<div class="alert alert-danger">Error while inserting data. Please try again!</div>';
					}
				}else{
					$set = array(
						'bc_id' => $bc,
						'user_id' => $user_id,
						'dashboard_bc_trans_year' => $year,
						'dashboard_bc_trans_added' => date_now(),
