	public function view_price_assumption($year = null){
		$info = $this->_require_login();

		$user_info = $this->get_user_info();
 		$bc_id = $user_info['bc_id'];
 		$bc_name = $user_info['cost_center_desc'];

 		if($year == null){
			$year = $this->_active_year();
		}

		$data['year'] = $year;
		$data['bc_name'] = $bc_name;
		$data['title'] = 'Price Assumption';
		$data['price'] = $this->price_assumption_report($bc_id, $year);

		$data['content'] = $this->load->view('bc/bc_view_price_assumption_content', $data , TRUE);
		$this->load->view('bc/templates', $data);
	}

