				/*$cost_center_id = $check_id['info']->cost_center_id;*/
				$cost_center_code = $check_id['info']->cost_center_code;
				$gl_name = $check_id['info']->gl_group_name;
				$gl_group_id = $check_id['info']->gl_group_id;

