			$where_budgeted = 'a.outlet_id NOT IN(SELECT outlet_id FROM sales_tbl x WHERE x.outlet_id=a.outlet_id AND x.sales_year=' . $year . ' AND x.sales_status=1 AND x.trans_type_id = 1)';

