					$ifs_code = $row_sales->ifs_code;
					$ifs_code = $row_sales->ifs_code;
					$ifs_code = $row_sales->ifs_code;
					$ifs_code = $row_sales->ifs_code;*/
					

					$spreadsheet->setActiveSheetIndex(0)
					->setCellValue("A" . $count, $bc_name)
