			 FROM prod_trans_tbl a, prod_trans_dtl_tbl b, component_type_tbl c, material_tbl d, material_tbl e, material_group_tbl f WHERE a.prod_trans_id = b.prod_trans_id AND b.component_type_id = c.component_type_id AND a.prod_id= " . $material_id . " AND a.prod_id = d.material_id AND b.article_id = e.material_id AND d.material_group_id = f.material_group_id AND b.article_type_id = 1 AND a.prod_trans_status = 3 AND a.bc_id = " . $bc_id . " AND b.prod_trans_dtl_date = '" . $date . "' AND b.prod_trans_dtl_status <> 5 AND a.brand_id = " . $brand_id . " AND a.process_type_id = 5 AND c.component_type = 'COST OF SALES'", TRUE);

			$total_cost = 0;
			$dressed_ave_wt = 1;
			$live_ave_wt = 1;
			$live_cost = 0;

			if(!empty($check_sku)){
				$total_cost = 0;
				$raw_mat = $check_sku->sku_material_id;
				$raw_mat_desc = $check_sku->mat_details;
				$live_ave_wt = $check_sku->live_ave_wt;
				$dressed_ave_wt = $check_sku->ave_wgt;
				$material_group = $check_sku->material_group_name;

				//Check if existing in external
				$join_ext = array(
					'ext_prod_trans_dtl_tbl b' => 'a.ext_prod_trans_id = b.ext_prod_trans_id AND a.ext_prod_trans_status = 1 AND b.ext_prod_trans_dtl_status = 1 AND a.bc_id = '. $bc_id . ' AND b.trans_dtl_date = "' . $date . '" AND a.material_id = ' . $raw_mat,
				);

				$check_ext_prod = $this->admin->check_join('ext_prod_trans_tbl a', $join_ext, TRUE);
				if($check_ext_prod['result'] == TRUE){
					$cost = $check_ext_prod['info']->cost;
					$ave_wgt = $check_ext_prod['info']->ave_wgt;
					/*if($ave_wgt != 0){
						$cost = $cost * $ave_wgt;
						$dressed_ave_wt = $ave_wgt;
					}*/

					$dressed_ave_wt = $ave_wgt;
					
					$total_cost = $cost;
					/*echo 'Product Cost: ' . $total_cost;
					echo '<br />';*/
				}else{
					$raw_mat_cost = 0;
