	public function bom_summary($bc_id, $date, $year, $get_material){
		$info = $this->_require_login();
		$user_info = $this->get_user_info();
 		$bc = $user_info['bc_id'];

 		$year = date('Y', strtotime($date));
 		$this->db->trans_start();
		foreach($get_material as $row){
			$material_id = $row->material_id;
			$brand_id = $row->brand_id;
			$material_desc = $row->material_desc;

			//Check BOM Summary table
			$where_bom = array('material_id' => $material_id, 'brand_id' => $brand_id, 'bc_id' => $bc_id, 'bom_summary_year' => $year, 'bom_summary_status' => 1);
			$check_summary = $this->admin->check_data('bom_summary_tbl', $where_bom, TRUE);
			if($check_summary['result'] == TRUE){
				$bom_summary_id = $check_summary['info']->bom_summary_id;
			}else{

				//Insert Summary
				$set_bom = array(
					'material_id' => $material_id,
					'brand_id' => $brand_id,
					'bc_id' => $bc_id,
					'bom_summary_year' => $year,
					'bom_summary_added' => date_now(),
					'bom_summary_status' => 1
				);

				$insert_summary = $this->admin->insert_data('bom_summary_tbl', $set_bom, TRUE);
				$bom_summary_id = $insert_summary['id'];
			}

			
			//Get SKU costing base on generated above and get raw materials
			$check_sku = $this->admin->get_query("SELECT e.material_id as sku_material_id, a.prod_trans_id, a.process_type_id, c.component_type, d.material_desc, d.material_id, e.material_desc as mat_details FROM prod_trans_tbl a, prod_trans_dtl_tbl b, component_type_tbl c, material_tbl d, material_tbl e WHERE a.prod_trans_id = b.prod_trans_id AND b.component_type_id = c.component_type_id AND a.prod_id= " . $material_id . " AND a.prod_id = d.material_id AND b.article_id = e.material_id AND b.article_type_id = 1 AND a.prod_trans_status = 3 AND a.bc_id = " . $bc_id . " AND b.prod_trans_dtl_date = '" . $date . "' AND b.prod_trans_dtl_status <> 5 AND a.brand_id = " . $brand_id . " AND a.process_type_id = 5 AND c.component_type = 'COST OF SALES'", TRUE);

			if(!empty($check_sku)){
				
				$get_bom = $this->db->query('
					SELECT e.material_id as sku_material_id, a.prod_trans_id, a.process_type_id, c.component_type, d.material_desc, d.material_id, e.material_desc as mat_details, b.rate, b.cost, b.amount_type_id FROM prod_trans_tbl a, prod_trans_dtl_tbl b, component_type_tbl c, material_tbl d, material_tbl e WHERE a.prod_trans_id = b.prod_trans_id AND b.component_type_id = c.component_type_id AND a.prod_id= ' . $material_id . ' AND a.prod_id = d.material_id AND b.article_id = e.material_id AND b.article_type_id = 1 AND a.prod_trans_status = 3 AND a.bc_id = ' . $bc_id . ' AND b.prod_trans_dtl_date = "' . $date . '" AND b.prod_trans_dtl_status <> 5 AND a.brand_id = ' . $brand_id . ' AND a.process_type_id = 5 AND c.component_type != \'COST OF SALES\' AND c.component_type != \'SALES REVENUE\'

						UNION ALL

						SELECT e.service_id as sku_material_id, a.prod_trans_id, a.process_type_id, c.component_type, d.material_desc, d.material_id, e.service_desc as mat_details, b.rate, b.cost, b.amount_type_id FROM prod_trans_tbl a, prod_trans_dtl_tbl b, component_type_tbl c, material_tbl d, services_tbl e WHERE a.prod_trans_id = b.prod_trans_id AND b.component_type_id = c.component_type_id AND a.prod_id= ' . $material_id . ' AND a.prod_id = d.material_id AND b.article_id = e.service_id AND b.article_type_id = 2 AND a.prod_trans_status = 3 AND a.bc_id = ' . $bc_id . ' AND b.prod_trans_dtl_date = "' . $date . '" AND b.prod_trans_dtl_status <> 5 AND a.brand_id = ' . $brand_id . ' AND a.process_type_id = 5
				')->result();
				
				$bom_total = 0;
				$bom_multiplier = 1;
				$sales_commision = 0;
				$amount_type = 0;
				foreach($get_bom as $row_bom){
					$bom_cost = $row_bom->cost;
					$bom_rate = $row_bom->rate;

					$bom_multiplier = 1;
					if($row_bom->component_type == "COST OF SALES" && $rate > 0){
						$bom_multiplier = $rate;

					}elseif($row_bom->component_type == 'SALES COMMISSION'){
						$amount_type = $row_bom->amount_type_id;
						$sales_commision += $bom_cost;
					}else{
						if($bom_rate > 0){
							$bom_total = $bom_total + ($bom_cost / $bom_rate);
						}else{
							$bom_total = $bom_total + $bom_cost;
						}
					}
				}
			}else{

				$sales_commision = 0;
				$amount_type = 0;
				$bom_multiplier = 1;
				$bom_total = 0;
			}

			$where_det = array('bom_summary_id' => $bom_summary_id, 'bom_summary_det_date' => $date, 'bom_summary_det_status' => 1);
			$check_bom_det = $this->admin->check_data('bom_summary_details_tbl', $where_det, TRUE);
			if($check_bom_det['result'] == TRUE){
				$bom_summary_det_id = $check_bom_det['info']->bom_summary_det_id;

				$set_det_total = array(
					'bom_summary_det_total' => $bom_total,
					'bom_summary_det_multi' => $bom_multiplier,
					'bom_summary_det_commision' => $sales_commision,
					'amount_type_id' => $amount_type,
				);
				$where_det_total = array('bom_summary_det_id' => $bom_summary_det_id);
				$update_det_total = $this->admin->update_data('bom_summary_details_tbl', $set_det_total, $where_det_total);
			}else{
				$set_det = array(
					'bom_summary_id' => $bom_summary_id,
					'bom_summary_det_total' => $bom_total,
					'bom_summary_det_multi' => $bom_multiplier,
					'bom_summary_det_commision' => $sales_commision,
					'amount_type_id' => $amount_type,
					'bom_summary_det_date' => $date,
					'bom_summary_det_added' => date_now(),
					'bom_summary_det_status' => 1
				);

				$insert_bom_det = $this->admin->insert_data('bom_summary_details_tbl', $set_det);
			}

			echo $material_desc . ' - ' . $bom_total . ' | ' . $bom_multiplier;
			echo '<br />';
			echo '<br />';
		}

		if($this->db->trans_status() === FALSE){
			$this->db->trans_rollback();
			$msg = '<div class="alert alert-danger">Error please try again!</div>';
		}else{
			$this->db->trans_commit();
			$msg = '<div class="alert alert-success">Price successfully adjusted.</strong></div>';
		}

	}

