	public function _get_segment_allocation($cost_center_group_id, $year, $date = null){

		if($date == null){
			$where_allocation = array('cost_center_group_id' => $cost_center_group_id, 'YEAR(dashboard_allocation_opex_date)' => $year);
		}else{
			$where_allocation = array('cost_center_group_id' => $cost_center_group_id, 'dashboard_allocation_opex_date' => $date);
		}

		$get_allocation = $this->admin->get_data('dashboard_allocation_opex_tbl', $where_allocation, TRUE, 'SUM(dashboard_allocation_opex_amount) as total_allocation');

		$allocation = $get_allocation->total_allocation;

		return $allocation;
	}

