 		if($broiler_cost_ave > $total_previous_actual_broiler_cost){
 			$broiler_cost_condition2 = 'higher';
 			$broiler_cost_indicator2 = 'fa fa-long-arrow-up';
 		}elseif($broiler_cost_ave < $total_previous_actual_broiler_cost){
