		$total_broiler = 0;
		$total_broiler1 = 0;
		$total_broiler2 = 0;
		for ($i=1; $i <=12 ; $i++) {
			$month = date('M', strtotime($year.'-'.$i.'-01'));
			$harvested_heads = $budget_harvested[$i];
