			(SELECT SUM(x.cost) FROM comparative_opex_gl_tbl x WHERE d.gl_sub_id = x.gl_sub_id AND x.bc_id = ' . $bc_id . ' AND x.comp_opex_gl_status = 1 AND YEAR(x.trans_year) = ' . ($year - 1) . ') as total_amount1,

