		');*/

		$opex_gl = $this->admin->get_query('

			SELECT opex.gl_group_name, opex.gl_sub_name, opex.gl_code, SUM(total) as total, SUM(total1) as total1, SUM(total2) as total2 
			FROM(

				(SELECT `e`.`gl_group_name`, `d`.`gl_sub_name`, d.gl_code, SUM(a.opex_amount) total, 0 as total1, 0 as total2

				FROM `gl_transaction_details_tbl` `a` JOIN `gl_transaction_item_tbl` `b` ON `a`.`gl_trans_item_id` = `b`.`gl_trans_item_id` AND `b`.`gl_trans_item_status` = 1 AND `a`.`gl_trans_det_status`=1 JOIN `gl_transaction_tbl` `c` ON `b`.`gl_trans_id`=`c`.`gl_trans_id` AND `c`.`gl_trans_status`=1 AND `c`.`gl_year`= ' . $year . ' JOIN `gl_subgroup_tbl` `d` ON `b`.`gl_sub_id`=`d`.`gl_sub_id` JOIN `gl_group_tbl` `e` ON `d`.`gl_group_id` = `e`.`gl_group_id` AND `e`.`gl_group_show` = 1 JOIN `cost_center_tbl` `f` ON `b`.`cost_center_id` = `f`.`cost_center_id` AND `f`.`cost_center_id` AND `f`.`parent_id`=' . $cost_center_id . ' GROUP BY `d`.`gl_code` ORDER BY `e`.`gl_group_name`
				)

				UNION

				(SELECT comp_tbl.gl_group_name, comp_tbl.gl_sub_name, comp_tbl.gl_code, SUM(comp_tbl.total) as total, SUM(comp_tbl.total1) as total1, SUM(comp_tbl.total2) as total2

				FROM
					(SELECT z.gl_group_name, y.gl_sub_name, y.gl_code, 0 as total, SUM(x.cost) as total1, 0 as total2 FROM comparative_opex_gl_tbl x, gl_subgroup_tbl y, gl_group_tbl z WHERE x.gl_sub_id = y.gl_sub_id AND y.gl_group_id = z.gl_group_id AND x.bc_id = ' . $bc_id . ' AND x.comp_opex_gl_status = 1 AND YEAR(x.trans_year) = '  . ($year - 1) . ' GROUP BY y.gl_code

					UNION


					SELECT z.gl_group_name, y.gl_sub_name, y.gl_code, 0 as total, 0 as total1, SUM(x.cost) as total2 FROM comparative_opex_gl_tbl x, gl_subgroup_tbl y, gl_group_tbl z WHERE x.gl_sub_id = y.gl_sub_id AND y.gl_group_id = z.gl_group_id AND x.bc_id = ' . $bc_id . ' AND x.comp_opex_gl_status = 1 AND YEAR(x.trans_year) = '  . ($year - 2) . ' GROUP BY y.gl_code
					) as comp_tbl

				GROUP BY comp_tbl.gl_code
				)
				) as opex
			GROUP BY opex.gl_code

		');

		$depre_sub = $this->get_depreciation_subgroup($cost_center_id, $year, $bc_id);
		$depre = "DEPRECIATION EXPENSES";
		foreach($depre_sub as $row_sub){
			$gl_sub_name = $row_sub->gl_sub_name;
			$depre_amount = $row_sub->total;
			$arr_depre = new stdClass;
			$arr_depre->gl_group_name = $depre;
			$arr_depre->gl_sub_name = $gl_sub_name;
			$arr_depre->total = $depre_amount;
			$arr_depre->total1 = 0;
			$arr_depre->total2 = 0;

			$key = array_search($gl_sub_name, array_column($opex_gl, 'gl_sub_name'));
			if(!empty($key)){
				$opex_gl[$key]->total += $depre_amount;
			}else{
				array_push($opex_gl, $arr_depre);	
			}
		}

		return $opex_gl;
	}

