		    $get_noi = $this->admin->check_data('dashboard_brand_summary_tbl', 'brand_id IN ' . $brand_list . ' AND dashboard_brand_summary_date = "' .  $month . '" AND bc_id = ' . $bc_id . ' AND dashboard_brand_summary_status = 1', TRUE, $select_noi_com);

		    $sales_unit[$a] = 0;
