		}else{
			$this->db->trans_commit();
			$msg = '<div class="alert alert-success"><strong>Transaction successfully '.$status.'.</strong></div>';
		}
		$this->session->set_flashdata('message', $msg);
		redirect('business-center/sales-bom-trans/' . $bc_id);
	}

