	    $total_dif2 = $total - $total2;

	    $total_per1 = $total1 != 0 ? ($total_dif1/$total1) * 100 : 0;
	    $total_per2 = $total2 != 0 ? ($total_dif2/$total2) * 100 : 0;

	    $opex_per_account_tbl .= '
